#!/bin/bash -e
#
echo "Building maven artifacts..." && cd /opt/app-root/application/mymassage-url/ && mvn  -e clean install -Dmaven.repo.local="/opt/app-root/.m2"  && cp -Rf /opt/app-root/application/mymassage-url/target/mymassage-url-0.0.1-SNAPSHOT.jar /opt/app-root/data/script && cd /opt/app-root/application/mymassage-util/ && mvn  -e clean install -Dmaven.repo.local="/opt/app-root/.m2"  && cd /opt/app-root/application//mymassage-ejb/ && mvn  -e clean install -Dmaven.repo.local="/opt/app-root/.m2"  && cd /opt/app-root/application//mymassage-war/ && mvn  -e clean install -Dmaven.repo.local="/opt/app-root/.m2"  && cd /opt/app-root/application//mymassage-ear/ && mvn  -e clean install -Dmaven.repo.local="/opt/app-root/.m2"  && cp -Rf /opt/app-root/application/mymassage-ear/target/mymassage-ear-0.0.1-SNAPSHOT.ear /opt/app-root && echo "Ends maven artifacts building..."